import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

export default function Register(){
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('student');
  const [error, setError] = useState('');
  const { register } = useAuth();
  const navigate = useNavigate();

  const submit = async (e) => {
    e.preventDefault();
    try {
      const data = await register({ name, email, password, role });
      if(data.role === 'teacher') navigate('/teacher');
      else navigate('/student');
    } catch(err){
      setError(err.response?.data?.message || 'Registration failed');
    }
  };

  return (<div style={{ba}}>
    <div className="container">
    <div style={{display: 'flex',justifyContent: 'center',alignItems: 'center',}}>
      <h2>Register</h2>
      </div>
      <div className="card">
        <form onSubmit={submit}>
        <label style={{marginTop:'10px'}}>Name</label>
          <input style={{marginTop:'10px'}} placeholder="Name" value={name} onChange={e=>setName(e.target.value)} required />
          <label style={{marginTop:'10px'}}>Email-id</label>
          <input style={{marginTop:'10px'}} placeholder="Email" value={email} onChange={e=>setEmail(e.target.value)} required />
          <label style={{marginTop:'10px'}}>Password</label>
          <input style={{marginTop:'10px'}} placeholder="Password" type="password" value={password} onChange={e=>setPassword(e.target.value)} required />
          <div className="flex" style={{marginTop:'10px'}}>
            <label > Student</label>
            <input style={{marginTop:'10px'}} type="radio" checked={role==='student'} onChange={()=>setRole('student')} />

              </div>
               <div className="flex" style={{marginTop:'10px'}}>
            <label> Teacher</label>
            <input style={{marginTop:'10px'}} type="radio" checked={role==='teacher'} onChange={()=>setRole('teacher')} />
        </div>
          {error && <div style={{color:'red'}}>{error}</div>}
          <hr/>
             <div style={{display: 'flex',justifyContent: 'center',alignItems: 'center', marginTop:'20px',marginBottom:'10px'}}>
          <div className="flex">
            <button className="btn" style={{width:'200px', backgroundColor: 'black', color: 'white', border: 'none',borderRadius:'2px',height:'40px'}} type="submit">Register</button>
          </div>
          </div>
        </form>
      </div>
    </div>
    </div>
  );
}
