import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

export default function Login(){
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const { login } = useAuth();
  const navigate = useNavigate();

  const submit = async (e) => {
    e.preventDefault();
    try {
      const data = await login(email, password);
      if(data.role === 'teacher') navigate('/teacher');
      else navigate('/student');
    } catch(err){
      setError(err.response?.data?.message || 'Login failed');
    }
  };

  return (
    <div className="container">
<div style={{display: 'flex',justifyContent: 'center',alignItems: 'center',}}>
<h2>Login</h2>
</div>

      <div className="card">
        <form onSubmit={submit}>
          <label>Email-id</label>  
          <input style={{marginTop:'10px'}} placeholder="Email" value={email} onChange={e=>setEmail(e.target.value)} required />
          <label>Password</label>  
          <input placeholder="Password" type="password" value={password} onChange={e=>setPassword(e.target.value)} required />
          {error && <div style={{color:'red'}}>{error}</div>}
          <div style={{display: 'flex',justifyContent: 'center',alignItems: 'center',}}>
          <div className="flex">
            <button className="btn btn-primary" type="submit">Login</button>
            <Link to="/register"><button type="button" className="btn">Register</button></Link>
          </div>
          </div>
        </form>
      </div>
    </div>
  );
}
