import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

export default function Register(){
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('student');
  const [error, setError] = useState('');
  const { register } = useAuth();
  const navigate = useNavigate();

  const submit = async (e) => {
    e.preventDefault();
    try {
      const data = await register({ name, email, password, role });
      if(data.role === 'teacher') navigate('/teacher');
      else navigate('/student');
    } catch(err){
      setError(err.response?.data?.message || 'Registration failed');
    }
  };

  return (
    <div className="container">
      <h2>Register</h2>
      <div className="card">
        <form onSubmit={submit}>
        <label>Name</label>
          <input placeholder="Name" value={name} onChange={e=>setName(e.target.value)} required />
          <label>Email-id</label>
          <input placeholder="Email" value={email} onChange={e=>setEmail(e.target.value)} required />
          label
          <input placeholder="Password" type="password" value={password} onChange={e=>setPassword(e.target.value)} required />
          <div className="flex">
            <label > Student</label>
            <input style={{marginTop:'10px'}} type="radio" checked={role==='student'} onChange={()=>setRole('student')} />

              </div>
               <div className="flex">
            <label> Teacher</label>
            <input style={{marginTop:'10px'}} type="radio" checked={role==='teacher'} onChange={()=>setRole('teacher')} />
        </div>
          {error && <div style={{color:'red'}}>{error}</div>}
          <div className="flex">
            <button className="btn btn-primary" type="submit">Register</button>
          </div>
        </form>
      </div>
    </div>
  );
}
