import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { API_BASE } from '../config';
import { useAuth } from '../context/AuthContext';
import dayjs from 'dayjs';

export default function TeacherDashboard(){
  const { user, logout } = useAuth();
  const [assignments, setAssignments] = useState([]);
  const [title, setTitle] = useState('');
  const [desc, setDesc] = useState('');
  const [due, setDue] = useState('');
  const [filter, setFilter] = useState('');
  const [selected, setSelected] = useState(null);
  const [submissions, setSubmissions] = useState([]);

  const fetch = async () => {
    const res = await axios.get(API_BASE + '/assignments' + (filter?('?status='+filter):''));
    setAssignments(res.data);
  };

  useEffect(()=>{ axios.defaults.headers.common['Authorization'] = 'Bearer ' + user.token; fetch(); }, []);

  const create = async () => {
    await axios.post(API_BASE + '/assignments', { title, description: desc, dueDate: due || null });
    setTitle(''); setDesc(''); setDue('');
    fetch();
  };

  const publish = async (id) => {
    await axios.put(API_BASE + '/assignments/'+id, { status: 'Published' });
    fetch();
  };

  const complete = async (id) => {
    await axios.put(API_BASE + '/assignments/'+id, { status: 'Completed' });
    fetch();
  };

  const remove = async (id) => {
    if(!window.confirm('Delete?')) return;
    await axios.delete(API_BASE + '/assignments/'+id);
    fetch();
  };

  const view = async (id) => {
    const res = await axios.get(API_BASE + '/assignments/'+id);
    setSelected(res.data.assignment);
    setSubmissions(res.data.submissions || []);
  };

  const markReviewed = async (sid) => {
    await axios.put(API_BASE + '/submissions/'+sid+'/review');
    view(selected._id);
  };

  return (
    <div className="container">
      <div style={{display:'flex',justifyContent:'space-between',alignItems:'center'}}>
        <h2>Teacher Dashboard</h2>
        <div>
          <strong>{user.name}</strong> <button className="btn" onClick={logout}>Logout</button>
        </div>
      </div>

      <div className="card">
        <h3>Create Assignment</h3>
        <input placeholder="Title" value={title} onChange={e=>setTitle(e.target.value)} />
        <textarea placeholder="Description" value={desc} onChange={e=>setDesc(e.target.value)} />
        <input type="date" value={due} onChange={e=>setDue(e.target.value)} />
        <div className="flex">
          <button className="btn btn-primary" onClick={create}>Create (Draft)</button>
        </div>
      </div>

      <div className="card">
        <h3>Your Assignments</h3>
        <div style={{marginBottom:8}}>
          Filter: <select value={filter} onChange={e=>setFilter(e.target.value)}>
            <option value="">All</option>
            <option>Draft</option>
            <option>Published</option>
            <option>Completed</option>
          </select>
          <button className="btn" onClick={fetch} style={{marginLeft:8}}>Refresh</button>
        </div>
        {assignments.map(a=>(
          <div key={a._id} className="card">
            <div style={{display:'flex',justifyContent:'space-between'}}>
              <div>
                <h4>{a.title} <small>({a.status})</small></h4>
                <div>Due: {a.dueDate ? dayjs(a.dueDate).format('YYYY-MM-DD') : '—'}</div>
                <div>{a.description}</div>
              </div>
              <div style={{minWidth:160}}>
                <div className="flex" style={{flexDirection:'column',gap:6}}>
                  {a.status==='Draft' && <button className="btn btn-primary" onClick={()=>publish(a._id)}>Publish</button>}
                  {a.status!=='Completed' && <button className="btn" onClick={()=>complete(a._id)}>Mark Completed</button>}
                  {a.status==='Draft' && <button className="btn btn-danger" onClick={()=>remove(a._id)}>Delete</button>}
                  <button className="btn" onClick={()=>view(a._id)}>View Submissions</button>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>

      {selected && <div className="card">
        <h3>Submissions for: {selected.title}</h3>
        {submissions.length===0 && <div>No submissions yet</div>}
        {submissions.map(s=>(
          <div key={s._id} style={{borderTop:'1px solid #eee',paddingTop:8}}>
            <div><strong>{s.student.name}</strong> ({s.student.email})</div>
            <div>Submitted: {dayjs(s.submittedAt).format('YYYY-MM-DD HH:mm')}</div>
            <div>{s.answer}</div>
            <div>Status: {s.reviewed ? 'Reviewed' : 'Pending'}</div>
            {!s.reviewed && <button className="btn" onClick={()=>markReviewed(s._id)}>Mark Reviewed</button>}
          </div>
        ))}
      </div>}
    </div>
  );
}
