import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { API_BASE } from '../config';
import { useAuth } from '../context/AuthContext';

export default function StudentDashboard() {
  const { user, logout } = useAuth();
  const [assignments, setAssignments] = useState([]);
  const [selected, setSelected] = useState(null);
  const [answer, setAnswer] = useState('');
  const [mine, setMine] = useState(null);

  const fetch = async () => {
    const res = await axios.get(API_BASE + '/assignments');
    setAssignments(res.data);
  };

  useEffect(() => {
    axios.defaults.headers.common['Authorization'] = 'Bearer ' + user.token;
    fetch();
  }, []);

  const view = async (id) => {
    const res = await axios.get(API_BASE + '/assignments/' + id);
    setSelected(res.data.assignment);
    const s = await axios.get(API_BASE + '/submissions/mine/' + id);
    setMine(s.data);
    setAnswer(s.data?.answer || '');
  };

  const submit = async () => {
    await axios.post(API_BASE + '/submissions/' + selected._id, { answer });
    const s = await axios.get(API_BASE + '/submissions/mine/' + selected._id);
    setMine(s.data);
  };

  return (
    <div className="container">
      {/* Header */}
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <h2>Student Dashboard</h2>
        <div>
          <strong>{user.name}</strong>{' '}
          <button className="btn" onClick={logout}>Logout</button>
        </div>
      </div>

      {/* Assignment List */}
      <div className="card">
        <h3>Published Assignments</h3>
        {assignments.map((a) => (
          <div key={a._id} className="card">
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
              <div>
                <h4>{a.title}</h4>
                <div>{a.description}</div>
                <div>Due: {a.dueDate ? new Date(a.dueDate).toISOString().slice(0, 10) : '—'}</div>
              </div>
              <div style={{ minWidth: 200, display: 'flex', gap: '8px' }}>
                {/* View button */}
                <button className="btn btn-secondary" onClick={() => view(a._id)}>
                  View
                </button>
                {/* Submit button */}
                <button className="btn btn-primary" onClick={() => view(a._id)}>
                  Submit
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* Assignment Details Section */}
      {selected && (
        <div className="card">
          <h3>{selected.title}</h3>
          <div>{selected.description}</div>
          <div>
            Due: {selected.dueDate ? new Date(selected.dueDate).toISOString().slice(0, 10) : '—'}
          </div>

          {/* If student has already submitted */}
          {mine ? (
            <div>
              <h4>Your Submission</h4>
              <div>{mine.answer}</div>
              <div>Submitted at: {new Date(mine.submittedAt).toLocaleString()}</div>
            </div>
          ) : (
            <div style={{ marginTop: '12px' }}>
              <textarea
                value={answer}
                onChange={(e) => setAnswer(e.target.value)}
                placeholder="Write your answer"
                style={{ width: '100%', height: '100px', marginBottom: '10px' }}
              />
              <button className="btn btn-primary" onClick={submit}>
                Submit (one-time)
              </button>
            </div>
          )}
        </div>
      )}
    </div>
  );
}
