import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { API_BASE } from '../config';
import { useAuth } from '../context/AuthContext';

export default function StudentDashboard() {
  const { user, logout } = useAuth();
  const [assignments, setAssignments] = useState([]); // All published assignments
  const [submittedAssignments, setSubmittedAssignments] = useState([]); // Already submitted
  const [selected, setSelected] = useState(null);
  const [answer, setAnswer] = useState('');
  const [mine, setMine] = useState(null);

  // Fetch assignments and submissions
  const fetchData = async () => {
    axios.defaults.headers.common['Authorization'] = 'Bearer ' + user.token;

    // All published assignments
    const resAssignments = await axios.get(`${API_BASE}/assignments`);
    setAssignments(resAssignments.data);

    // All submissions of this student
    const resSubmissions = await axios.get(`${API_BASE}/submissions/mine`);
    setSubmittedAssignments(resSubmissions.data); 
    // Expected response: array of { assignmentId, answer, submittedAt, ... }
  };

  useEffect(() => {
    fetchData();
  }, []);

  // View assignment details
  const viewAssignment = async (id) => {
    const res = await axios.get(`${API_BASE}/assignments/${id}`);
    setSelected(res.data.assignment);

    // Check if student has already submitted this assignment
    const submission = await axios.get(`${API_BASE}/submissions/mine/${id}`);
    setMine(submission.data);
    setAnswer(submission.data?.answer || '');
  };

  // Submit answer (only if not submitted yet)
  const submitAnswer = async () => {
    if (mine) return; // already submitted
    if (!answer.trim()) {
      alert('Please write an answer before submitting.');
      return;
    }

    await axios.post(`${API_BASE}/submissions/${selected._id}`, { answer });

    // Refresh submission list and selected
    const submission = await axios.get(`${API_BASE}/submissions/mine/${selected._id}`);
    setMine(submission.data);
    fetchData();
  };

  // Helper: check if an assignment has already been submitted
  const isSubmitted = (assignmentId) => {
    return submittedAssignments.some((s) => s.assignmentId === assignmentId);
  };

  return (
    <div className="container">
      {/* Header */}
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <h2>Student Dashboard</h2>
        <div>
          <strong>{user.name}</strong>{' '}
          <button className="btn" onClick={logout}>Logout</button>
        </div>
      </div>

      {/* Assignments List */}
      <div className="card" style={{ marginTop: 12 }}>
        <h3>All Assignments</h3>
        {assignments.map((a) => {
          const submitted = isSubmitted(a._id);
          return (
            <div key={a._id} className="card" style={{ marginBottom: 8, backgroundColor: submitted ? '#f0f0f0' : 'white' }}>
              <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                <div>
                  <h4>{a.title} {submitted && <span style={{ fontSize: '12px', color: 'green' }}>Submitted</span>}</h4>
                  <div>{a.description}</div>
                  <div>Due: {a.dueDate ? new Date(a.dueDate).toISOString().slice(0, 10) : '—'}</div>
                </div>
                <div style={{ minWidth: 150 }}>
                  <button className="btn btn-secondary" onClick={() => viewAssignment(a._id)}>
                    View
                  </button>
                  {!submitted && (
                    <button className="btn btn-primary" onClick={() => viewAssignment(a._id)} style={{ marginLeft: 4 }}>
                      Submit
                    </button>
                  )}
                </div>
              </div>
            </div>
          );
        })}
      </div>

      {/* Selected Assignment Details */}
      {selected && (
        <div className="card" style={{ marginTop: 16 }}>
          <h3>{selected.title}</h3>
          <div>{selected.description}</div>
          <div>Due: {selected.dueDate ? new Date(selected.dueDate).toISOString().slice(0, 10) : '—'}</div>

          {mine ? (
            // Already submitted → read-only
            <div style={{ marginTop: 12 }}>
              <h4>Your Submission (read-only)</h4>
              <textarea
                value={mine.answer}
                readOnly
                style={{ width: '100%', height: '100px', backgroundColor: '#f0f0f0' }}
              />
              <div>Submitted at: {new Date(mine.submittedAt).toLocaleString()}</div>
            </div>
          ) : (
            // Not submitted → allow submission
            <div style={{ marginTop: 12 }}>
              <textarea
                value={answer}
                onChange={(e) => setAnswer(e.target.value)}
                placeholder="Write your answer"
                style={{ width: '100%', height: '100px', marginBottom: 8 }}
              />
              <button className="btn btn-primary" onClick={submitAnswer}>
                Submit (one-time)
              </button>
            </div>
          )}
        </div>
      )}
    </div>
  );
}
